
public class CharDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		char ch = 'a';
		char chi = 65;
		
		System.out.println(ch);
		System.out.println(chi);
		
		char chl = '\u00A7';
		System.out.println(chl);
		
		char chli = '\u20AC';
		System.out.println(chli);
		
		int a = 'A';
		System.out.println(a);
	}

}
